import { createApp } from 'vue'
import { createRouter, createWebHashHistory } from 'vue-router'
import App from './App.vue'

// Bootstrap y estilos
import 'bootstrap/dist/css/bootstrap.min.css'
import 'bootstrap/dist/js/bootstrap.bundle.min.js'
import 'bootstrap-icons/font/bootstrap-icons.css'

// AOS (Animate On Scroll)
import AOS from 'aos'
import 'aos/dist/aos.css'

// FontAwesome configuraci贸n
import { library } from '@fortawesome/fontawesome-svg-core'
import { FontAwesomeIcon } from '@fortawesome/vue-fontawesome'
import { faCircleQuestion, faChevronUp, faChevronDown, faXmark } from '@fortawesome/free-solid-svg-icons'

library.add(faCircleQuestion, faChevronUp, faChevronDown, faXmark)

// Importar vistas
import HomePage from './views/HomePage.vue'
import AboutPage from './views/AboutPage.vue'
import ContactPage from './views/ContactPage.vue'
import CoberturaPage from './views/CoberturaPage.vue'
import ConocenosPage from './views/ConocenosPage.vue'

// Configurar rutas
const routes = [
    { path: '/', component: HomePage },
    { path: '/about', component: AboutPage },
    { path: '/contact', component: ContactPage },
    { path: '/cobertura', component: CoberturaPage },
    { path: '/conocenos', component: ConocenosPage },
    { path: '/:pathMatch(.*)*', redirect: '/' },
]

const router = createRouter({
    history: createWebHashHistory(),
    routes
})

// Crear aplicaci贸n
const app = createApp(App)

// Registrar el componente globalmente
app.component('font-awesome-icon', FontAwesomeIcon)

// Usar router y montar la app
app.use(router)

// Inicializar AOS antes de montar la app
AOS.init()
app.mount('#app')
